import React from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';

const ContactUs = ({ content }) => {
  // Example default content (if no content prop is passed)
  const defaultContent = {
    title: 'Contact Us',
    description: 'Feel free to reach out to us for any inquiries or feedback.',
    address: '123 Company St, City, Country',
    email: 'info@example.com',
    phone: '+1234567890',
  };

  // Use the passed content or default content if not provided
  const {
    title,
    description,
    address,
    email,
    phone
  } = content || defaultContent;
  const backgroundStyle = {
    backgroundImage: `url("data:image/jpeg;base64,")`, // Replace with your image URL
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    color: 'white', // Adjust text color for better contrast
    padding: '50px 0',
  };

  return (
    <div style={backgroundStyle}>
    <Container className="mt-5">
      <h2>{title}</h2>
      <p>{description}</p>

      <Row>
        <Col md={6}>
          <h4>Contact Information</h4>
          <p><strong>Address:</strong> {address}</p>
          <p><strong>Email:</strong> {email}</p>
          <p><strong>Phone:</strong> {phone}</p>
        </Col>
        <Col md={6}>
          <h4>Send us a Message</h4>
          <Form>
            <Form.Group controlId="formName">
              <Form.Control type="text" placeholder="Your Name" />
            </Form.Group>
            <Form.Group controlId="formEmail">
              <Form.Control type="email" placeholder="Your Email" />
            </Form.Group>
            <Form.Group controlId="formMessage">
              <Form.Control as="textarea" rows={3} placeholder="Your Message" />
            </Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
    </div>
  );
};

export default ContactUs;
